    async construirArmas(arma) {
        // Inicializa el stack general que contendr√° todos los elementos relacionados con un arma de fuego
        let stackGeneral = [];
    
        // Subt√≠tulo est√°tico y tabla de armas de fuego dentro de un stack unbreakable
        const stackArmaConSubtitulo = {
            unbreakable: true,
            stack: [
                // Subt√≠tulo est√°tico
                {
                    text: 'Arma de fuego secuestrada',
                    style: 'title',
                    margin: [0, 10, 0, 5]
                },
                // Construcci√≥n de la tabla de datos del arma
                this.construirTablaAF(arma)
            ],
            margin: [0, 5, 0, 5]
        };
    
        stackGeneral.push(stackArmaConSubtitulo);
    
        // Agregar los datos complementarios
        const stackDatosComplementarios = await this.construirDatosComplementarios(arma.DatosComplementarios);
        if (stackDatosComplementarios) {
            stackGeneral.push(stackDatosComplementarios);
        }
    
        // Retorna el stack general que contiene todos los elementos relacionados con el arma de fuego.
        return {
            stack: stackGeneral
        };
    }

